import React,{Component} from 'react';
import './App.css';
import web3 from './web3';
import sample from './sample';

class App extends Component {

  constructor(props){
     super(props);
    this.state = {manager: ''};
  }

 async componentDidMount(){
    const manager = await sample.methods.manager().call();
    this.setState({manager});

  }
  render(){
    return(
      <div className="container" >
      &nbsp; &nbsp;   <h1>Smart Contract  </h1>
      <p> Attempting to deploy from accoount {this.state.manager} </p>
      <p>A smart contract is a computer protocol intended to digitally facilitate,
         verify, or enforce the negotiation or performance of a contract. ... <br/>
          The aim of smart contracts is to provide security that is superior to traditional contract <br/>
           law and to reduce other transaction costs associated with contracting.</p>
         <hr/>
          <b>Want to send some ether ? </b>
         <form>
         &nbsp; &nbsp; <label> Amount of ether to send</label>
         &nbsp; &nbsp; <input type="text" /> <br/>
         &nbsp; &nbsp; <button > send </button>
         </form>
         <hr/>
      </div>

    );
  }

}


export default App;
